+	deobfCompile "slimeknights.mantle:Mantle:1.12-1.3.1.18"
+	deobfCompile "slimeknights:TConstruct:1.12-2.7.2.15"
+	deobfCompile "cofh:RedstoneFlux:1.12-2.0.0.1:universal"
+}
+
+task myCopy << {
+	ant.replaceregexp(match:'aaa', replace:'bbb', flags:'g', byline:true) {
+		fileset(dir: 'src/main/java/android/app/cfg', includes: 'TestingConfigCopy.java')
+	}
+}
+
+processResources {
+	exclude '**/*.xcf'
