+    Map<Traversal<String>, Collection<TraversalEmbedding>> traversalEmbeddings = Maps.newHashMap();
+
+    // FOR EACH VERTEX
+    for (Map.Entry<GradoopId, AdjacencyListRow<IdWithLabel, IdWithLabel>> outgoingRow :
