+                // Didn't work, trying without ms
+                sdf = new SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ss'Z'");
+                sdf.setTimeZone(TimeZone.getTimeZone("UTC"));
+
+                return new DateTimeOffset(sdf.parse(stringValue));
+            } catch (ParseException ex) {
+                return null;
+            }
         }
     }
 }
