+/**
+ * Class for maintaining the Rolit game.
+ *
+ * @author Sophie Lathouwers & Kevin Alberts
+ * @version $Revision: 1.0 $
+ */
+public class NetworkGame extends Game {
+
+    // -- Constructors -----------------------------------------------
+    /**
+     * Creates a new Game object.
+     * @param players - ArrayList of players that are participating
+     */
+    public NetworkGame(ArrayList<Player> players) {
+    	super(players);
+    }
+
+    // -- Commands ---------------------------------------------------
+
+    /**
+     * Starts the Rolit game. <br>
+     */
+    public void start() {
+    	reset();
+    	play();
+    }
+
+    /**
+     * Plays the Rolit game. <br>
+     * First the (still empty) board is shown. Then the game is played until it
+     * is over. Players can make a move one after the other. After each move,
+     * the changed game situation is printed.
+     */
+    private void play() {
+        playOneMove();
+    }
+
+    /**.
+     * Performs one move and notifies the observers
+     */
+    public void playOneMove() {
+    	int keuze = playersArray[current].makeMove(board);
+    	this.lastMoveMade = keuze;
+    	current = (current + 1) % numPlayers;
+    	update();
+        setChanged();
+        notifyObservers();
+    }
+
+    /**
+     * @return gets current player
+     */
+    public Player getPlayer() {
+    	return this.playersArray[current];
+    }
+
+    /**
+     * Prints the game situation.
+     */
+    private void update() {
+        System.out.println("\ncurrent game situation: \n\n" + board.toString()
+                + "\n");
+        int[] possibleMoves = board.getValidFields(board, this.playersArray[this.current].getMark());
+        System.out.println("Possible moves: " + Board.intArrayToString(possibleMoves));
+    }
+
+    /**
+     * Prints the result of the last game. <br>
+     */
+    private void printResult() {
+    	testBoard.debug(board.countSquares(board).toString());
+        if (board.hasWinner()) {
+            Player winner = null;
+            for (int i = 0; i < 4; i++) {
+            	if (board.isWinner(playersArray[i].getMark())) {
+            		winner = playersArray[i];
+            	}
+            }
+            System.out.println("Player " + winner.getName() + " ("
+                    + winner.getMark().toString() + ") has won!");
+        } else {
+            System.out.println("Draw. There is no winner!");
+        }
+    }
+
+    /**.
+     * returns current player's mark
+     * @return Mark of current player
+     */
+    public Mark getCurrentPlayer() {
+    	return this.playersArray[this.current].getMark();
+    }
+
+    /**.
+     * returns the board of this game
+     * @return board
+     */
+    public Board getBoard() {
+    	return this.board;
+    }
+
+    /**.
+     * gets number of participating players
+     * @return number of players participating
+     */
+    public int getPlayerCount() {
+    	return this.numPlayers;
+    }
+
+    /**.
+     * resets the game
+     */
+	public void resetGame() {
+		this.reset();
+	}
+}

