+/**
+ * Class for maintaining a human player in Rolit.
+ *
+ * @author Sophie Lathouwers & Kevin Alberts
+ * @version $Revision: 1.0 $
+ */
+public class NetworkPlayer extends Player {
+	/**.
+	 * variable to check whether a move has been done already
+	 */
+	public boolean moveDone = false;
+	//public Object lock = new Object();
+	public ReentrantLock lock = new ReentrantLock();
+	/**.
+	 * variable to store the chosen move
+	 */
+	private int chosenMove;
+	/**
+	 * variable to store the ClientHandler for this player
+	 */
+	private ClientHandler client = null;
+    // -- Constructors -----------------------------------------------
+    /**
+     * Creates a new Network player object.
+     * @param name - name of Player
+     * @param mark - mark of Player
+     */
+    public NetworkPlayer(String name, Mark mark) {
+        super(name, mark);
+    }
+    /**
+     * Creates a new Network player object.
+     * @param name - ClientHandler for player
+     * @param mark - mark of Player
+     */
+    public NetworkPlayer(ClientHandler client, Mark mark){
+    	this(client.getNick(), mark);
+    	this.client = client;
+    }
+
+    // -- Commands ---------------------------------------------------
+    /**
+     * Asks the user to input the field where to place the next mark. This is
+     * done using the standard input/output. \
+     *
+     * @param board - the game board
+     * @return the player's chosen field
+     */
+    public int determineMove(Board board) {
+    	this.moveDone = false;
+    	this.client.clientInfo.networkGame.determineMove(this);
+    	Logger.log(Logger.DEBUG, "Entering loop in awaitance of move.");
+    	
+    	synchronized(this){
+    		try{
+    			while(!this.moveDone){
+    				this.wait();
+    			}
+    		}catch (InterruptedException e){
+    			
+    		}
+    	}
+		
+    	Logger.log(Logger.DEBUG, "Gotten move! The move is "+this.chosenMove);
+    	return this.chosenMove;
+    }
+
+    /**.
+     * checks if move is valid
+     * @param choice - move that the player wants to do
+     * @param b - board
+     * @return boolean - whether move is valid or not
+     */
+    public boolean isMoveValid(int choice, Board b) {
+    	int[] fields = b.getValidFields(b, this.getMark());
+    	for (int i : fields) {
+    		if (i == choice) {
+    			return true;
+    		}
+    	}
+    	return false;
+    }
+
+    /**.
+     * sets chosenMove
+     * @param j field that is chosen
+     */
+	public void chooseMove(int j) {
+		this.chosenMove = j;
+		this.moveDone = true;
+		synchronized(this){
+			this.notifyAll();
+		}
+		
+	}
+	
+	/**
+	 * returns the player's ClientHandler
+	 */
+	public ClientHandler getClient(){
+		return this.client;
+	}
+}

